        // Adds an image signature to the PDF
        private void OnAddImageSignatureClicked(object sender, EventArgs e)
        {
            int pageNumber = 1;

            // Define the position and size of the image signature on the page
            RectF bounds = new RectF(50, 450, 120, 40);

            // Load the image from embedded resources
            Stream? imageStream = this.GetType().Assembly.GetManifestResourceStream("Signature.Assets." + "image.png");

            // Create a stamp annotation using the image and mark it as a signature
            StampAnnotation customStamp = new StampAnnotation(imageStream!, pageNumber, bounds);
            customStamp.IsSignature = true;

            // Add the image signature to the PDF viewer
            PdfViewer.AddAnnotation(customStamp);

        }
    }

